SELECT GROUP_CONCAT(DISTINCT a.tradeNo) FROM gw_suspicious_trans_order_info a LEFT JOIN gw_suspicious_mer_trans_info b ON b.Id=a.syspId WHERE FIND_IN_SET(b.ruleId,#{ruleIdList}) AND b.createDate=#{createDate} AND b.susType=#{susType}
